[0]₁
  = { def [m]ₙ }
{ x : x ∈ Z, x ≣ 0 (mod 1) }
  = { def (mod n) }
{ x : x ∈ Z, x mod 1 = 0 mod 1 }
  = { 0 mod 1 = 0 }
{ x : x ∈ Z, x mod 1 = 0 }
  = { ∀ x ∈ Z: 1 | x }
{ x : x ∈ Z }
  = { def Z }
Z

[0]₂
  = { def [m]ₙ }
{ x : x ∈ Z, x = 0 (mod 2) }
  = { def (mod n) }
{ x : x ∈ Z, x mod 2 = 0 mod 2 }
  = { 0 mod 2 = 0 }
{ x : x ∈ Z, x mod 2 = 0 }
  = { x mod 2 = 0 ⇒ ∃ k ∈ Z: x = 2 ∗ k }
{ 2 ∗ x : x ∈ Z }
  = { def 2Z }
2Z

[1]₂
  = (def [m]n)
{ x : x ∈ Z, x = 1 (mod 2) }
  = (def (mod n))
{ x : x ∈ Z, x mod 2 = 1 mod 2 }
  = (1 mod 2 = 1)
{ x : x ∈ Z, x mod 2 = 1 }
  = (x mod 2 = 1 ⇒ ∃ k ∈ Z: x = 2 ∗ k + 1)
{ 2 ∗ x + 1 : x ∈ Z }
  = (def 2Z+1)
2Z+1

